/**
 * Installer base class.
 */
abstract class AbstractShareModuleInstaller extends AbstractExtensionInstaller
{
    /**
     * Install the MUShareModule application.
     *
     * @return boolean True on success, or false
     *
     * @throws RuntimeException Thrown if database tables can not be created or another error occurs
     */
    public function install()
    {
        $logger = $this->container->get('logger');
        $userName = $this->container->get('zikula_users_module.current_user')->get('uname');
    
        // create all tables from according entity definitions
        try {
            $this->schemaTool->create($this->listEntityClasses());
        } catch (\Exception $exception) {
            $this->addFlash('error', $this->__('Doctrine Exception') . ': ' . $exception->getMessage());
            $logger->error('{app}: Could not create the database tables during installation. Error details: {errorMessage}.', ['app' => 'MUShareModule', 'errorMessage' => $exception->getMessage()]);
    
            return false;
        }
    
        // set up all our vars with initial values
        $this->setVar('maxRadius', '5000');
        $this->setVar('maxLocations', '5');
        $this->setVar('locationEntriesPerPage', '10');
        $this->setVar('linkOwnLocationsOnAccountPage', true);
        $this->setVar('offerEntriesPerPage', '10');
        $this->setVar('linkOwnOffersOnAccountPage', true);
        $this->setVar('poolEntriesPerPage', '10');
        $this->setVar('linkOwnPoolsOnAccountPage', true);
        $this->setVar('messageEntriesPerPage', '10');
        $this->setVar('linkOwnMessagesOnAccountPage', true);
        $this->setVar('enabledFinderTypes', [ 'location' ,  'offer' ,  'pool' ,  'message' ]);
        $this->setVar('defaultLatitude', '0.00');
        $this->setVar('defaultLongitude', '0.00');
        $this->setVar('defaultZoomLevel', '5');
        $this->setVar('tileLayerUrl', 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png');
        $this->setVar('tileLayerAttribution', '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors');
        $this->setVar('enableLocationGeoLocation', false);
        $this->setVar('enableOfferGeoLocation', false);
    
        $categoryRegistryIdsPerEntity = [];
    
        // add default entry for category registry (property named Main)
        $categoryHelper = new \MU\ShareModule\Helper\CategoryHelper(
            $this->container->get('translator.default'),
            $this->container->get('request_stack'),
            $logger,
            $this->container->get('zikula_users_module.current_user'),
            $this->container->get('zikula_categories_module.category_registry_repository'),
            $this->container->get('zikula_categories_module.api.category_permission')
        );
        $categoryGlobal = $this->container->get('zikula_categories_module.category_repository')->findOneBy(['name' => 'Global']);
        $entityManager = $this->container->get('doctrine.orm.default_entity_manager');
    
        $registry = new CategoryRegistryEntity();
        $registry->setModname('MUShareModule');
        $registry->setEntityname('OfferEntity');
        $registry->setProperty($categoryHelper->getPrimaryProperty('Offer'));
        $registry->setCategory($categoryGlobal);
    
        try {
            $entityManager->persist($registry);
            $entityManager->flush();
        } catch (\Exception $exception) {
            $this->addFlash('error', $this->__f('Error! Could not create a category registry for the %entity% entity.', ['%entity%' => 'offer']));
            $logger->error('{app}: User {user} could not create a category registry for {entities} during installation. Error details: {errorMessage}.', ['app' => 'MUShareModule', 'user' => $userName, 'entities' => 'offers', 'errorMessage' => $exception->getMessage()]);
        }
        $categoryRegistryIdsPerEntity['offer'] = $registry->getId();
    
        // initialisation successful
        return true;
    }
    
