    /**
     * Renames the module name for variables in the module_vars table.
     */
    protected function updateModVarsTo14()
    {
        $conn = $this->getConnection();
        $conn->update('module_vars', ['modname' => 'MUShareModule'], ['modname' => 'Share']);
    }
    
    /**
     * Renames this application in the core's extensions table.
     */
    protected function updateExtensionInfoFor14()
    {
        $conn = $this->getConnection();
        $conn->update('modules', ['name' => 'MUShareModule', 'directory' => 'MU/ShareModule'], ['name' => 'Share']);
    }
    
    /**
     * Renames all permission rules stored for this app.
     */
    protected function renamePermissionsFor14()
    {
        $conn = $this->getConnection();
        $componentLength = strlen('Share') + 1;
    
        $conn->executeQuery("
            UPDATE group_perms
            SET component = CONCAT('MUShareModule', SUBSTRING(component, $componentLength))
            WHERE component LIKE 'Share%';
        ");
    }
    
    /**
     * Renames all category registries stored for this app.
     */
    protected function renameCategoryRegistriesFor14()
    {
        $conn = $this->getConnection();
        $componentLength = strlen('Share') + 1;
    
        $conn->executeQuery("
            UPDATE categories_registry
            SET modname = CONCAT('MUShareModule', SUBSTRING(modname, $componentLength))
            WHERE modname LIKE 'Share%';
        ");
    }
    
    /**
     * Renames all (existing) tables of this app.
     */
    protected function renameTablesFor14()
    {
        $conn = $this->getConnection();
    
        $oldPrefix = 'share_';
        $oldPrefixLength = strlen($oldPrefix);
        $newPrefix = 'mu_share_';
    
        $sm = $conn->getSchemaManager();
        $tables = $sm->listTables();
        foreach ($tables as $table) {
            $tableName = $table->getName();
            if (substr($tableName, 0, $oldPrefixLength) != $oldPrefix) {
                continue;
            }
    
            $newTableName = str_replace($oldPrefix, $newPrefix, $tableName);
    
            $conn->executeQuery("
                RENAME TABLE $tableName
                TO $newTableName;
            ");
        }
    }
    
    /**
     * Removes event handlers from database as they are now described by service definitions and managed by dependency injection.
     */
    protected function dropEventHandlersFromDatabase()
    {
        \EventUtil::unregisterPersistentModuleHandlers('Share');
    }
    
    /**
     * Updates the module name in the hook tables.
     */
    protected function updateHookNamesFor14()
    {
        $conn = $this->getConnection();
    
        $conn->update('hook_area', ['owner' => 'MUShareModule'], ['owner' => 'Share']);
    
        $componentLength = strlen('subscriber.share') + 1;
        $conn->executeQuery("
            UPDATE hook_area
            SET areaname = CONCAT('subscriber.musharemodule', SUBSTRING(areaname, $componentLength))
            WHERE areaname LIKE 'subscriber.share%';
        ");
    
        $conn->update('hook_binding', ['sowner' => 'MUShareModule'], ['sowner' => 'Share']);
    
        $conn->update('hook_runtime', ['sowner' => 'MUShareModule'], ['sowner' => 'Share']);
    
        $componentLength = strlen('share') + 1;
        $conn->executeQuery("
            UPDATE hook_runtime
            SET eventname = CONCAT('musharemodule', SUBSTRING(eventname, $componentLength))
            WHERE eventname LIKE 'share%';
        ");
    
        $conn->update('hook_subscriber', ['owner' => 'MUShareModule'], ['owner' => 'Share']);
    
        $componentLength = strlen('share') + 1;
        $conn->executeQuery("
            UPDATE hook_subscriber
            SET eventname = CONCAT('musharemodule', SUBSTRING(eventname, $componentLength))
            WHERE eventname LIKE 'share%';
        ");
    }
    
    /**
     * Updates the module name in the workflows table.
     */
    protected function updateWorkflowsFor14()
    {
        $conn = $this->getConnection();
        $conn->update('workflows', ['module' => 'MUShareModule'], ['module' => 'Share']);
        $conn->update('workflows', ['obj_table' => 'LocationEntity'], ['module' => 'MUShareModule', 'obj_table' => 'location']);
        $conn->update('workflows', ['obj_table' => 'OfferEntity'], ['module' => 'MUShareModule', 'obj_table' => 'offer']);
        $conn->update('workflows', ['obj_table' => 'PoolEntity'], ['module' => 'MUShareModule', 'obj_table' => 'pool']);
        $conn->update('workflows', ['obj_table' => 'MessageEntity'], ['module' => 'MUShareModule', 'obj_table' => 'message']);
    }
    
    /**
     * Returns connection to the database.
     *
     * @return Connection the current connection
     */
    protected function getConnection()
    {
        $entityManager = $this->container->get('doctrine.orm.default_entity_manager');
    
        return $entityManager->getConnection();
    }
    
    /**
     * Uninstall MUShareModule.
     *
     * @return boolean True on success, false otherwise
     *
     * @throws RuntimeException Thrown if database tables or stored workflows can not be removed
     */
    public function uninstall()
    {
        $logger = $this->container->get('logger');
    
        try {
            $this->schemaTool->drop($this->listEntityClasses());
        } catch (\Exception $exception) {
            $this->addFlash('error', $this->__('Doctrine Exception') . ': ' . $exception->getMessage());
            $logger->error('{app}: Could not remove the database tables during uninstallation. Error details: {errorMessage}.', ['app' => 'MUShareModule', 'errorMessage' => $exception->getMessage()]);
    
            return false;
        }
    
        // remove all module vars
        $this->delVars();
    
        // remove category registry entries
        $entityManager = $this->container->get('doctrine.orm.default_entity_manager');
        $registries = $this->container->get('zikula_categories_module.category_registry_repository')->findBy(['modname' => 'MUShareModule']);
        foreach ($registries as $registry) {
            $entityManager->remove($registry);
        }
        $entityManager->flush();
    
        // uninstallation successful
        return true;
    }
    
    /**
     * Build array with all entity classes for MUShareModule.
     *
     * @return array list of class names
     */
    protected function listEntityClasses()
    {
        $classNames = [];
        $classNames[] = 'MU\ShareModule\Entity\LocationEntity';
        $classNames[] = 'MU\ShareModule\Entity\OfferEntity';
        $classNames[] = 'MU\ShareModule\Entity\OfferCategoryEntity';
        $classNames[] = 'MU\ShareModule\Entity\PoolEntity';
        $classNames[] = 'MU\ShareModule\Entity\MessageEntity';
    
        return $classNames;
    }
